import './App.css';
import { useReducer, useState } from 'react';
import React from 'react';
import Task from './Task.js';
import User from './User.js';

const defaultUser = new User;
const App=()=>{
  const [date, setDate]  = useState('mm/dd/yyyy');
  const [time, setTime]  = useState('hh:mm AM/PM');
  const [title, setTitle]  = useState('');
  const [description, setDescription]  = useState('');
  const [year, setYear] = useState('');
  return (
    <div
    className='bg'>
      {taskCreate([date, setDate], [time, setTime], [title, setTitle], [description, setDescription])}
      {Calendar( [year, setYear])}
    </div>
  );
}
const taskCreate = ([date, setDate], [time, setTime], [title, setTitle], [description, setDescription]) =>{
  const options = [['Date:', 'Time:', 'Title:', 'Description:'],['65', '65', '67.5', '42', '50','55'],['25', '25', '22.5', '48', '40', '35'],['text', 'text',  'text', 'text', 'text'], ['mm/dd/yyyy', 'hh:mm AM/PM','', '', 'none']]
  const getStyle = (i) => {
    return({
      left:`${options[2][i]}%`, 
      width: `${options[1][i]}%`,
      top: `${10*i + 10}%`
    });
  }
  const handleSubmit = (e) => {
    e.preventDefault();
    if(date.split("/")[0].length == 2 && date.split("/")[1].length == 2 && date.split("/")[2].length == 4 && !isNaN(date.split("/")[0])&& !isNaN(date.split("/")[1])&& !isNaN(date.split("/")[2])){
      let task = new Task;
      task.newTask(date, time, title, description);
      defaultUser.addTask(task);
    } else alert("Your date is likely formated incorrectly. Try doing so in the mm/dd/yyyy format");
  }
  return(
    <div
    className='taskCreateWindow'>
        <form onSubmit={handleSubmit}>
            <label
            className='taskCreateTitle'>
                Create Task
            </label>
            {
                options[0].map((option, i) => 
                    <label 
                    className='taskCreateTxt' 
                    style={{top: `${10*i + 10}%`}}>
                        {option}
                    </label>
                )
            }
            <input
            className="taskCreateInput"
            type="text"
            required
            value={date}
            onChange = {(e)=>{setDate(e.target.value)}}
            style = {getStyle(0)}
            />
            <input
            className="taskCreateInput"
            type="text"
            required
            value={time}
            onChange = {(e)=>{setTime(e.target.value)}}
            style = {getStyle(1)}
            />
            <input
            className="taskCreateInput"
            type="text"
            required
            value={title}
            onChange = {(e)=>{setTitle(e.target.value)}}
            style = {getStyle(2)}
            />
            <input
            className="taskCreateInput"
            type="text"
            required
            value={description}
            onChange = {(e)=>{setDescription(e.target.value)}}
            style = {getStyle(3)}
            />
            <button
            style = {{position: 'absolute', top: '80%', height: '10%', width: '40%', left: '30%'}}>
              Create
            </button>
        </form>
    </div>
  );
}
const Calendar = ([year, setYear]) => {
  let today = new Date();
  let currYear = String(today.getFullYear());
  let style;
  const mOfYear = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
  const monthFocus=(month)=>{
      console.log(month);
  }
  const getMonths=()=>{
    let Months=[];
    for(let x = 0; x < 12; x++){
      Months.push(x);
    }
    return(Months);
  }
  return(
    <div 
    className="Calendar">
      <body 
      className="Year">
        {`${year}`}
      </body>
      {
        getMonths().map(month => {
            if(month < 4){
            style = {
                top: `13%`,
                left: `${24.67*(month%4)+(month%4)*.3}%`
            }
            } else if(month < 8){
            style = {
                top: `42%`,
                left: `${24.67*(month%4)+(month%4)*.3}%`
            }
            } else{
            style = {
                top: `71%`,
                left: `${24.67*(month%4)+(month%4)*.3}%`
            }
            }
            return(
              <div 
              className="Month" style = {style}>
                  <body 
                  className = "mText">
                    {mOfYear[month]}
                  </body>
                  <button 
                  className = 'monthButton' 
                  onClick = {() => monthFocus(month)} >
                  </button>
              </div>
            );
        })
      }
      <button className='taskCreateInput' style = {{position: 'absolute', left: '35%', width: '2em', top: "3%"}} onClick = {()=>{setYear(year--);}}>←</button>
      <button className='taskCreateInput' style = {{position: 'absolute', left: '57%', width: '2em', top: "3%"}} onClick = {()=>{currYear++; console.log(currYear)}}>→</button>
    </div>
  );
}
export default App;
