        }
    }
    state = {
        value: 3
    }
    render(){
        return(
            <div className='taskCreateWindow'>
                <form>
                    <label className='taskCreateTitle'>
                        Create Task
                    </label>
                    {
                        options[0].map((option, i) => 
                            <label className='taskCreateTxt' style={{top: `${10*i + 10}%`}}>
                                {option}
                            </label>
                        )
                    }
                    {
                        options[0].map((option, i) => 
                        <input defaultValue={options[4][i]} onChange={(evt)=>console.log(evt.target.value)} max={5} min={1} type={options[3][i]} className=' taskCreateInput' style={{left: '10%', top: `${10*i + 11}%`, height: '5%', width: `${options[1][i]}%`, left: `${options[2][i]}%`}}>
                        
                        </input>
                    )
                    }
                    <input type="range" min="1" max="5" defaultValue={3} class="slider" id="prioritySlide" onChange={({ target: { value: radius } }) => this.prioChange(radius)}></input>
                    <label id="priority" className='taskCreateTxt' style={{top: '61%'}} > Priority: {this.state.value}</label>
                    <select defaultValue={''} className='taskCreateInput' style={{top: '75%', width: '40%', height: '10%', left: '10%'}}>
                        <option value={'default'}></option>
                        <option value={'+new'}>+new</option>
                    </select>
                    <input type ="submit" value="Create" className='taskCreateInput' style={{bottom: '10%', left: '35%', width: '30%', textAlign: 'center'}} onSubmit={()=>this.handleSubmit()}/>
                </form>
            </div>
        );
    }
}
