import React from 'react';
import ReactDOM from 'react-dom';
import Task from './Task.js';
import Calendar from './Calendar.js';

let options = [['Date:', 'Time:', 'Title:', 'Description:'],['65', '65', '67.5', '42', '50','55'],['25', '25', '22.5', '48', '40', '35'],['text', 'text',  'text', 'text', 'text'], ['mm/dd/yyyy', 'hh:mm AM/PM','', '', 'none']]
let prioChange;
export default class TaskCreate extends React.Component{
    prioChange = (input) =>{
        this.setState({priority: input});
    }
    handleSubmit = () =>{
        alert(this.state);
        Calendar.addTask(new Task(this.state.date,this.state.time,this.state.title,this.state.description,this.state.category,this.state.priority));
    }
    handleChange=(index, value)=>{
        switch(index){
            case 0:
                this.setState({date: value});
            case 1:
                this.setState({time: value});
                break;
            case 2:
                this.setState({title: value});
                break;
            case 3:
                this.setState({description: value});
                break;
            case 4:
                this.setState({category: value});
                break;
            case 5:
                this.setState({priority: value});
                break;
        }
    }
    state = {
        priority: 3
    }
    render(){
        return(
            <div className='taskCreateWindow'>
                <form onSubmit={(e)=>{e.preventDefault()}}>
                    <label className='taskCreateTitle'>
                        Create Task
                    </label>
                    {
                        options[0].map((option, i) => 
                            <label className='taskCreateTxt' style={{top: `${10*i + 10}%`}}>
                                {option}
                            </label>
                        )
                    }
                    {
                        options[0].map((option, i) => 
                        <input defaultValue={options[4][i]} onChange={(evt)=>this.handleChange(i, evt.target.value)} max={5} min={1} type={options[3][i]} className=' taskCreateInput' style={{left: '10%', top: `${10*i + 11}%`, height: '5%', width: `${options[1][i]}%`, left: `${options[2][i]}%`}}>
                        
                        </input>
                    )
                    }
                    <input type="range" min="1" max="5" defaultValue={3} className="slider" id="prioritySlide" onChange={({ target: { value: radius } }) => this.prioChange(radius)}></input>
                    <label id="priority" className='taskCreateTxt' style={{top: '61%'}} > Priority: {this.state.priority}</label>
                    <label className='taskCreateTxt' style={{top: '51%'}}>Category:</label>
                    <select className='taskCreateInput' style={{top: '51%', width: '55%', height: '8%', left: '40%'}}>
                        <option value={'default'}></option>
                        <option value={'+new'}>+new</option>
                    </select>
                    <input type ="submit" value="Create" className='taskCreateInput' style={{bottom: '10%', left: '35%', width: '30%', textAlign: 'center'}} onClick={()=>this.handleSubmit()}/>
                </form>
            </div>
        );
    }
}
