let month;
let day;
let year;
let prio = 1;
let description;
export default class Task{
    newTask = (date, time, title, description) =>{
        month = date.split("/")[0];
        day = date.split("/")[1];
        year = date.split("/")[2];
        this.description = description;
    }
    getYear(){
        return year;
    }
    getMonth(){
        return month;
    }
    getDay(){
        return day;
    }
    getPrio(){
        return prio;
    }
    getDescription(){
        return description;
    }
}
