export default class Task{
    newTask = (date, time, title, descIn) =>{
        this.month = date.split("/")[0];
        this.day = date.split("/")[1];
        this.year = date.split("/")[2];
        this.description = descIn;
        this.prio = 1;
    }
    getYear(){
        return this.year;
    }
    getMonth(){
        return this.month;
    }
    getDay(){
        return this.day;
    }
    getPrio(){
        return this.prio;
    }
    getDescription(){
        return this.description;
    }
}
