import logo from './logo.svg';
import './App.css';
import Front from './front.js';
import { useState } from 'react';


const App=()=>{
  const [date, setDate]  = useState('');
  const [time, setTime]  = useState('');
  return (
    <div
    className='bg'>
      {taskCreate([date, setDate], [time, setTime])}
      {Calendar()}
    </div>
  );
}
const taskCreate = ([date, setDate], [time, setTime]) =>{
  const options = [['Date:', 'Time:', 'Title:', 'Description:'],['65', '65', '67.5', '42', '50','55'],['25', '25', '22.5', '48', '40', '35'],['text', 'text',  'text', 'text', 'text'], ['mm/dd/yyyy', 'hh:mm AM/PM','', '', 'none']]
  const getStyle = (i) => {
    return({
      left:`${options[2][i]}%`, 
      width: `${options[1][i]}%`,
      top: `${10*i + 10}%`
    });
  }
  return(
    <div
    className='taskCreateWindow'>
        <form>
            <label
            className='taskCreateTitle'>
                Create Task
            </label>
            {
                options[0].map((option, i) => 
                    <label 
                    className='taskCreateTxt' 
                    style={{top: `${10*i + 10}%`}}>
                        {option}
                    </label>
                )
            }
            <input
            className="taskCreateInput"
            type="text"
            required
            value={date}
            onChange = {(e)=>{setDate(e.target.value)}}
            style = {getStyle(0)}
            />
            <input
            className="taskCreateInput"
            type="text"
            required
            value={time}
            onChange = {(e)=>{setTime(e.target.value)}}
            style = {getStyle(1)}
            />
            <input
            className="taskCreateInput"
            type="text"
            required
            value={date}
            onChange = {(e)=>{setDate(e.target.value)}}
            style = {getStyle(0)}
            />
            <input
            className="taskCreateInput"
            type="text"
            required
            value={date}
            onChange = {(e)=>{setDate(e.target.value)}}
            style = {getStyle(0)}
            />
        </form>
    </div>
  );
}
//<input defaultValue={options[4][i]} onChange={(evt)=>this.handleChange(i, evt.target.value)} max={5} min={1} type={options[3][i]} className=' taskCreateInput' style={{left: '10%', top: `${10*i + 11}%`, height: '5%', width: `${options[1][i]}%`, left: `${options[2][i]}%`}}>
const Calendar = () => {
  let today = new Date();
  let currDay = String(today.getDay()-1)
  let currMonth = String(today.getMonth()+1);
  let currYear = String(today.getFullYear());
  let style;
  const mOfYear = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
  const monthFocus=(month)=>{
      this.setState({focus: 100+month});
  }
  const getMonths=()=>{
    let Months=[];
    for(let x = 0; x < 12; x++){
      Months.push(x);
    }
    return(Months);
  }
  return(
    <div 
    className="Calendar">
                <body 
                className="Year">
                  {`${currYear}`}
                </body>
                {
                getMonths().map(month => {
                    if(month < 4){
                    style = {
                        top: `13%`,
                        left: `${24.67*(month%4)+(month%4)*.3}%`
                    }
                    } else if(month < 8){
                    style = {
                        top: `42%`,
                        left: `${24.67*(month%4)+(month%4)*.3}%`
                    }
                    } else{
                    style = {
                        top: `71%`,
                        left: `${24.67*(month%4)+(month%4)*.3}%`
                    }
                    }
                    return(
                    <div 
                    className="Month" style = {style}>
                        <body 
                        className = "mText">
                          {mOfYear[month]}
                        </body>
                        <button 
                        className = 'monthButton' 
                        onClick = {() => this.monthFocus(month)} >

                        </button>
                    </div>
                    );
                })
                }
            </div>
  );
}
export default App;
