import Task from './Task.js';
import React from 'react';
import Calendar from './Calendar.js';

let Tasks = [];
let calendar = new Calendar();
export default class User extends React.Component{
    state = {
        userTasks: this.Tasks
    }
    addTask(input){
        let temp = Tasks;
        temp.push(input);
        Tasks = temp;
        calendar.addTask(input);
        this.setState({userTasks: this.Tasks})
        Tasks.forEach(task =>{
            console.log(task.getDescription());
        })
    }
    getTasks(){
        return Tasks;
    }
    getCalendar(){
        return calendar;
    }
}
