import React from 'react';
import ReactDOM from 'react-dom';

let options = [['Date:', 'Time:', 'Title:', 'Description:', 'Category:'],['65', '65', '67.5', '42', '50','55'],['25', '25', '22.5', '48', '40', '35'],['text', 'text',  'text', 'text', 'text', 'range']]
let style;
var slider = document.getElementById("prioritySlide");
var output = document.getElementById("priority");
output.innerHTML = slider.value;
slider.oninput = function() {
    output.innerHTML = this.value;
}
export default class TaskCreate extends React.Component{
    render(){
        return(
            <div className='taskCreateWindow'>
                <form>
                    <label className='taskCreateTitle'>
                        Create Task
                    </label>
                    {
                        options[0].map((option, i) => 
                            <label className='taskCreateTxt' style={{top: `${10*i + 10}%`}}>
                                {option}
                            </label>
                        )
                    }
                    {
                        options[0].map((option, i) => 
                        <input max={5} min={1} type={options[3][i]} className=' taskCreateInput' style={{left: '10%', top: `${10*i + 11}%`, height: '5%', width: `${options[1][i]}%`, left: `${options[2][i]}%`}}>
                        
                        </input>
                    )
                    }
                    <input type="range" min="1" max="5" value="2" class="slider" id="prioritySlide"></input>
                </form>
            </div>
        );
    }
}
