import Day from "./Day.js";
import Task from "./Task.js";
import Year from "./Year";
import Month from './Month';

const Calendar=()=>{
    const datesWithTasks = [];
    const addTask=(taskIn)=>{
        let flag = false;
        for(let x = 0; x < datesWithTasks.length; x++){
            if(taskIn.getYear()==datesWithTasks[x].getYear()){
                for(let y = 0; y < datesWithTasks[x].getYear().getMonths().length; y++){
                    if(taskIn.getMonth()==datesWithTasks[x].getYear().getMonths()[y].getMonth()){
                        for(let i = 0; i < datesWithTasks[x].getYear().getMonths()[y].getMonth().getDays().length; i++){
                            if(taskIn.getDay()==datesWithTasks[x].getYear().getMonths()[y].getMonth().getDays()[i].getDay()){
                                datesWithTasks[x].getYear().getMonths()[y].getMonth().getDays()[i].getDay().addTask(taskIn);
                                flag = true;
                            }
                        }
                        if(!flag){
                            let day = new Day();
                            day.newDay(taskIn.getDay());
                            day.addTask(taskIn);
                            datesWithTasks[x].getYear().getMonths()[y].getMonth().addDay(day);
                            flag = true;
                        }
                    }
                }
                if(!flag){
                    let month = new Month();
                    month.newMonth(taskIn.getMonth());
                    let day = new Day();
                    day.newDay(taskIn.getDay());
                    day.addTask(taskIn);
                    month.addDay(day);
                    datesWithTasks[x].getYear().addMonth(month);
                    flag = true;
                }
            }
        }
        if(!flag){
            let year = new Year();
            year.newYear(taskin.getYear);
            let month = new Month();
            month.newMonth(taskIn.getMonth());
            let day = new Day();
            day.newDay(taskIn.getDay());
            day.addTask(taskIn);
            month.addDay(day);
            year.addMonth(month);
            datesWithTasks[x].addYear(year);
            flag = true;
        }
    }
}
export default Calendar;
