import logo from './logo.svg';
import './App.css';
import Front from './front.js';

const taskCreate = () =>{
  const options = [['Date:', 'Time:', 'Title:', 'Description:'],['65', '65', '67.5', '42', '50','55'],['25', '25', '22.5', '48', '40', '35'],['text', 'text',  'text', 'text', 'text'], ['mm/dd/yyyy', 'hh:mm AM/PM','', '', 'none']]
  return(
    <div
    className='taskCreateWindow'>
        <form>
            <label
            className='taskCreateTitle'>
                Create Task
            </label>
            {
                options[0].map((option, i) => 
                    <label 
                    className='taskCreateTxt' 
                    style={{top: `${10*i + 10}%`}}>
                        {option}
                    </label>
                )
            }
            <input
            type="text"
            required
            />
        </form>
    </div>
  );
}
const Calendar = () => {
  let today = new Date();
  let currDay = String(today.getDay()-1)
  let currMonth = String(today.getMonth()+1);
  let currYear = String(today.getFullYear());
  const mOfYear = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
  const monthFocus=(month)=>{
      this.setState({focus: 100+month});
  }
  return(
    <div className="Calendar">
                <body className="Year">
                {`${currYear}`}
                </body>
                {
                getMonths().map(month => {
                    if(month < 4){
                    style = {
                        top: `13%`,
                        left: `${24.67*(month%4)+(month%4)*.3}%`
                    }
                    } else if(month < 8){
                    style = {
                        top: `42%`,
                        left: `${24.67*(month%4)+(month%4)*.3}%`
                    }
                    } else{
                    style = {
                        top: `71%`,
                        left: `${24.67*(month%4)+(month%4)*.3}%`
                    }
                    }
                    return(
                    <div className="Month" style = {style}>
                        <body className = "mText">
                        {mOfYear[month]}
                        </body>
                        <button className = 'monthButton' onClick = {() => this.monthFocus(month)} ></button>
                    </div>
                    );
                })
                }
            </div>
  );
}

function App() {
  return (
    <div
    className='bg'>
      {taskCreate()}
    </div>
  );
}
/*<div className='bg'>
                <Calendar id = 'cal'/>
                <TaskCreate id = 'tCreate'/>
            </div>
*/
export default App;
