import Day from './Day.js';

let month;
let days = [];

export default class Month{
    newMonth(monthIn){
        month = monthIn;
    }
    addDay(dayIn){
        days.push(dayIn);
    }
    getMonth(){
        return month;
    }
    getDays(){
        return days;
    }
    getTotalPrio(){
        let prio;
        days.forEach(day =>{
            day.getTasks().forEach(task=>{
                prio+=task.getPrio();
            });
        });
        return prio;
    }
}
