export default class Task{
    month;
    day;
    year;
    prio = 1;
    description;
    newTask = (date, time, title, descIn) =>{
        month = date.split("/")[0];
        day = date.split("/")[1];
        year = date.split("/")[2];
        description = descIn;
    }
    getYear(){
        return year;
    }
    getMonth(){
        return month;
    }
    getDay(){
        return day;
    }
    getPrio(){
        return prio;
    }
    getDescription(){
        return description;
    }
}
