import React from 'react';
import ReactDOM from 'react-dom';

let options = [['Date:', 'Time:', 'Priority:', 'Title:', 'Description:', 'Category:'],['10', '10', '10', '10', '10', '10',],[]]
let style;
let getInputBoxes = () =>{
    console.log("it ran")
    options[0].forEach((option, i) =>{
        style = {
            top: `${15*i}%`,
            width: `${options[1][i]}%`
        }
        return(
                <input className='taskCreateInput' type='text' style={style}/>
        );
    });
}
export default class TaskCreate extends React.Component{
    render(){
        return(
            <div className = "taskCreateWindow">
                <body className = 'taskCreateTitle' top = '10%'>
                    New Task
                </body>
                {
                    getInputBoxes()
                }
            </div>
        );
    }
}
