import Task from './Task.js';

let day;
let tasks = [];

export default class Day{
    newDay(DayIn){
        day = DayIn;
    }
    addTask(taskIn){
        tasks.push(taskIn);
        console.log(tasks[0].getDescription());
        console.log(tasks[1].getDescription());
    }
    getDay(){
        return day;
    }
    getTasks(){
        return tasks;
    }
}
