import React from 'react';
import ReactDOM from 'react-dom';
import Task from './Task.js';

let today = new Date();
let currDay = String(today.getDay()-1)
let currMonth = String(today.getMonth()+1);
let currYear = String(today.getFullYear());
const mOfYear = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];
let style;
let getMonths=()=>{
  let Months=[];
  for(let x = 0; x < 12; x++){
    Months.push(x);
  }
  return(Months);
}
export default class Calendar extends React.Component{
    addTask = (Task)=>{
         console.log('it ran');
    }
    state = {
        // year focused = -(year), month = 100+month. Ex(January = 100), day = day (the twelvth = 12)
        focus: -currYear,
        happy: 190
    }
    monthFocus=(month)=>{
        this.setState({focus: 100+month});
    }
    render(){
        return (
            <div className="Calendar">
                <body className="Year">
                {`${currYear}`}
                </body>
                {
                getMonths().map(month => {
                    if(month < 4){
                    style = {
                        top: `13%`,
                        left: `${24.67*(month%4)+(month%4)*.3}%`
                    }
                    } else if(month < 8){
                    style = {
                        top: `42%`,
                        left: `${24.67*(month%4)+(month%4)*.3}%`
                    }
                    } else{
                    style = {
                        top: `71%`,
                        left: `${24.67*(month%4)+(month%4)*.3}%`
                    }
                    }
                    return(
                    <div className="Month" style = {style}>
                        <body className = "mText">
                        {mOfYear[month]}
                        </body>
                        <button className = 'monthButton' onClick = {() => this.monthFocus(month)} ></button>
                    </div>
                    );
                })
                }
            </div>
        );
    }
};
